#define PI 3.1415926535898
#define RED (vec3(0.639, 0.122, 0.133))
#define WHITE (vec3(0.949, 0.922, 0.851))
#define YELLOW (vec3(0.98, 0.769, 0.141))
#define BLUE (vec3(0.016, 0.384, 0.62))
#define BLACK (vec3(0.0, 0.0, 0.0))



mat2 Rotate2D(in float angle) {
  return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

vec2 Tile(in vec2 uv, in float zoom) {
  return fract(uv * zoom);
}

float Strafta(in vec2 uv, in bool bReverse) {
  uv = Tile(uv, 2.0);
  uv *= iResolution.y/8.5;
  uv = Rotate2D(PI * 0.25)*uv;
  return bReverse ? step(fract(uv.y), 0.5) : step(0.5, fract(uv.y));
}

float HLine(in float pos, in float thickness, in float x) {
  return step(pos - thickness, x) * step(x, pos + thickness);
}

float CrossLine(in float pos, in float thickness, in vec2 uv, in float strafta1, in float strafta2) {
  uv = Tile(uv, 2.0);
  float L1 = HLine(pos, thickness, uv.x);
  vec2 uvRot = Rotate2D(PI * 0.5) * uv;
  float L2 = HLine(pos, thickness, uvRot.x);
  return L1 * strafta1 + L2 * strafta2;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    // uv = (Rotate2D(iTime * 0.25) * uv) * (1.2 + 0.7 * sin(iTime));

    float strafta1 = Strafta(uv, false);
    float strafta2 = Strafta(uv, true);

    float fBlack = CrossLine(0.5, 0.32, uv, strafta1, strafta2);
    float fRed = CrossLine(0.5, 0.03, uv, strafta1, strafta2);
    fRed += CrossLine(0.425, 0.025, uv, strafta1, strafta2);
    fRed += CrossLine(0.575, 0.025, uv, strafta1, strafta2);
    float fWhite = CrossLine(0.5, 0.008, uv, strafta1, strafta2);
    fWhite += CrossLine(0.3, 0.008, uv, strafta1, strafta2);
    fWhite += CrossLine(0.7, 0.008, uv, strafta1, strafta2);
    float fYellow = CrossLine(0.27, 0.008, uv, strafta1, strafta2);
    fYellow += CrossLine(0.73, 0.008, uv, strafta1, strafta2);
    float fBlue = CrossLine(0.18, 0.014, uv, strafta1, strafta2);
    fBlue += CrossLine(0.82, 0.014, uv, strafta1, strafta2);

    vec3 color = RED;
    color = mix(color, BLACK, fBlack);
    color = mix(color, RED, fRed);
    color = mix(color, WHITE, fWhite);
    color = mix(color, YELLOW, fYellow);
    color = mix(color, BLUE, fBlue);

    fragColor = vec4(color, 1.0);
}
