// variant of https://shadertoy.com/view/dlGfzG
// golfing and messing with "random triangular grid circles" by jt. https://shadertoy.com/view/DtVBDm

#define T(i) (2.*iTime+H(i,))
#define H(i,o) fract(1e4*sin(dot( d+P[i] , o+R )))
#define L(i)   1.73 * length( p-d - P[i] )                      \
             -   mix( H(i,floor(T(i))), H(i,ceil(T(i))) ,fract(T(i)))


void mainImage(out vec4 O, vec2 I)
{
    vec3  R = iResolution,
          p =    mat2x3( 7,-7, 0, 4,4,-8 )
               * 4.*I / R.y + .33,               
          d = floor(p); 
    mat3  P = mat3(1);
    d.x+d.y+d.z < 0. ? P = 1.-P : P;
    //  insert abs here  v  for bubbles
    O = sqrt( R.yyyy/1e2* ( min(min(L(0),L(1)),L(2)) - .25 ));
 // O.r = -(d.x+d.y+d.z); // show tiling
}
