void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 screenSize = iResolution.xy;
    float time = iTime;

    vec2 uv = fragCoord.xy / screenSize;
    vec2 center = vec2(0.5, 0.5);
    float radius = 0.25;

    // Calculate the offset based on time
    float t = time * 2.0 * 3.14159;
    float offsetX = sin(t) * 0.25;
    float offsetY = cos(t) * 0.25;

    // Apply the offset to the circle center
    center.x += offsetX;
    center.y += offsetY;

    float distance = length(uv - center);

    // Set the color to white if the pixel is within the circle, black otherwise
    vec3 color = (distance <= radius) ? vec3(1.0) : vec3(0.0);

    fragColor = vec4(color, 1.0);
}
