vec3 aquamarin = vec3 (0.470588, 0.858823, 0.886275);
vec3 sunglow = vec3 (1.,0.811764,0.282352);
vec3 peach = vec3 (1.,0.811764,0.670588);
vec3 springgreen = vec3 (0.92549, 0.917647, 0.745098);
vec3 violet = vec3 (0.450980, 0.4, 0.741176);
vec3 navyblue = vec3 (0.09803, 0.454902, 0.823529);
vec3 white = vec3 (1., 1., 1.);
vec3 green = vec3 (0.698039, 0.92549, 0.3647);
vec3 red = vec3 (1., 0., 0.);

vec2 rotate(vec2 uv, float th) {
  return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

vec3 Background(vec2 uv) {
    return aquamarin;
}

float circle(vec2 uv, float r, vec2 move) {
  float x = uv.x - move.x;
  float y = uv.y - move.y;
  return length(vec2(x, y)) - r;
}

float square(vec2 uv, float r, vec2 move) {
  float x = uv.x - move.x;
  float y = uv.y - move.y;
  vec2 rotated = rotate(vec2(x,y), iTime);
  float d = abs(rotated.x) + abs(rotated.y) - r;
  return d;
}

vec3 drawScene(vec2 uv) {
  float x = uv.x;
  float y = uv.y;
  vec3 col = Background(uv);
  vec2 move = vec2(sin(iTime*2.)*0.65, cos(iTime)*0.3);
  vec2 move1 = vec2(sin(iTime*2.+3.)*0.65, cos(iTime)*0.3);
  if (y < 0.5*x + 0.35 && y > 0.875*x - 0.6125 && y > -0.35 && y > -0.875*x - 0.6125 && y < -0.5*x + 0.35 ) col=sunglow;
  float circle = circle(uv, 0.14, move);
  float square = square(uv, 0.16, move1);
  col = mix(violet, col, step(0., circle));
  col = mix(red, col, step(0., square));
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy; // <0,1>
  uv -=0.5;
  uv.x *= iResolution.x/iResolution.y;
  vec3 col = drawScene(uv);

  fragColor = vec4(col,1.0);
}
