vec3 palette(in float t)
{
//[[0.618 0.964 0.859] [0.465 0.267 0.462] [0.091 0.329 1.321] [0.690 3.426 0.831]]
    vec3 a = vec3(0.618, 0.964, 0.859);
    vec3 b = vec3(0.465, 0.267, 0.462);
    vec3 c = vec3(0.091, 0.329, 1.321);
    vec3 d = vec3(0.690, 3.426, 0.831);
    return a + b * cos(4.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i < 3.0; i++) {
        uv = fract(uv * 2.0) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 color = palette(length(uv0) + i + 0.5*iTime);

        d = sin(5.0*d + 0.2*iTime) / 5.0;
        d = abs(d);
        d = 0.005126 / d;

        finalColor += color * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
