#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)


void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,0.6));                    
 	for(float i=0.,s,e,g=0.,t=iTime;i++<80.;){
        p=g*d;
        p.z-=.4;
        p=R(p,H(t*.03), t*.032);
        p.x-=.1;
        p=(atan(abs(p-0.00))); 
        s=1.5;
        for(int j=0;j++<8;)
           s*=e=max(.5/dot(p,p),2.3*0.451*(1.+0.2*atan(iTime*0.2 )  )),
           p=abs(p*e-vec3(2,3,2)*0.58),                                
           p=R(p,normalize(vec3(3,3,1)),1.8);
        g+=e=abs(p.z)/(s  )+1e-4*0.5;                               
	    c+=mix(vec3(1),H((log(s)+5.*atan(iTime*0.2 ))*.4),.25)*.03/exp(1.8*i*i*e);       
	}                           
	c*=c*c*c;
    O=vec4(c,1);
}
