void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 6.* abs( u+u - R ) / R.y,
         I = round(U),                                                                 // tile id
         C = floor(U),                                                                 // diamond id
         F = U-C -.5, S;  
    F += vec2(-F.y,F);                                                                 // diamond tilted coords
    S = sign(F); F = abs(F);                                                           // diamond sector


    O = mix( int(I+I.y)%2 < 1 ? vec4(.3,.73,.5,1) :  vec4(.63,.84,.2,1),               // checkers
            (S.x==S.y) == int(C+C.y+ceil(iTime))%2 < 1 ? vec4(.8,0,.56,1) : vec4(1) ,  // diamond
            smoothstep(25./R.y, 0., max(F.x,F.y)-.2 ) );
}
