// This shader computes the distance to the Mandelbrot Set for everypixel, and colorizes
// it accoringly.
// 
// Z -> ZÂ²+c, Z0 = 0. 
// therefore Z' -> 2Â·ZÂ·Z' + 1
//
// The Hubbard-Douady potential G(c) is G(c) = log Z/2^n
// G'(c) = Z'/Z/2^n
//
// So the distance is |G(c)|/|G'(c)| = |Z|Â·log|Z|/|Z'|
//
// More info here: https://iquilezles.org/articles/distancefractals

// contribution by Jason Wilkins - Tup/2016 - Generalized Mandelbrot and distance function

// === the magic function.  ==========================================
#define db_display(X,Y,val) {                                             \
    vec2 U = vec2(15,10)* ( gl_FragCoord.xy/iResolution.xy - vec2(X,Y) ); \
    st_assert( pFloat(U,val).x < .5 );                                    \
}

// === fonts utils from https://www.shadertoy.com/view/llySRh =========
// ( can't be hidden in Common because, you know, can't access textures there for odd reasons.)

vec4 char(vec2 p, int c) {// --- display char
    vec2 dFdx = dFdx(p/16.), dFdy = dFdy(p/16.);
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx, dFdy );
}
float dx = .4;
vec4 pInt(vec2 p, float n) {// --- display int
    vec4 v = vec4(0);
    if (n < 0.) 
        v += char(p - vec2(-.5,0), 45 ),
        n = -n;

    for (float i = 3.; i>=0.; i--) 
        n /=  9.999999, // 10., // for windows :-(
        v += char(p - dx*vec2(i,0), 48+ int(fract(n)*10.) );
    return v;
}
vec4 pFloat(vec2 p, float n) { // --- display float4.4
    vec4 v = vec4(0);
    if (n < 0.) v += char(p - vec2(-.5,0), 45 ), n = -n;
    v += pInt(p,floor(n)); p.x -= 4.*dx; // comment this line in abs(n) < 1
    v += char(p, 46);      p.x -= dx;
    v += pInt(p,fract(n)*1e4);
    return v;
}

vec2 refine(float xx, float yy, float time) 
{
    float ax = pow(xx,
        cos(
            xx+pow(yy,
                sin(
                    pow(yy,
                        cos(xx+time)
                    )
                )
            )
        )
    );
    float ay = pow(yy,
        sin(
            yy+pow(xx,
                cos(xx+yy)
            )
        )
    );
    return vec2(ax,ay);
}

float check(vec2 p, float time) {
    float ax = (p.x);
    float ay = (p.y);
    return (
    (ax*pow(ay,
        cos(ax+sin(ay)))
        )
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //float x = fragCoord.x;
    //float y = fragCoord.y;

    vec2 point = vec2(fragCoord.x/100.0,fragCoord.y/50.0);

    // animation	
	float t = -iTime/5.0;

    float color = float(1);
    float hold = float(1);
    // iterate
    for( int i=0; i<256; i++ )
    {
        float value = check(point,t);

        if( (abs(value) > 10.0) || (abs(value) < 0.1) ) {
            break;
        }

        //color = abs(value);

        point = refine(float(point.x),float(point.y),t);
    }

    color = check(point,t)/100.0;
    vec3 col = vec3( float(color) );

    fragColor = vec4(col, 1.0 );
}
