void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy*sin(fragCoord.xy))*2.0/iResolution.xy;
    vec3 col=vec3(1,1,1);
    float r=(sin(uv.x*60.0+iTime*4.0)*cos(uv.x*10.0+0.0)-sin(uv.y*1.0));
    float g=1.0-uv.y*uv.y*(sin(uv.x*10.0-(iTime*0.2)));
    g= g*uv.y*uv.y*abs(tanh(uv.x*2.0-(iTime*0.5)));
    float b=abs(sin(uv.x*60.0+iTime*4.0))*cos(uv.x*20.0+1.0)-sin(uv.y);
    r= r*2.0*uv.x*uv.x*(tan(uv.y*10.0-(iTime*0.1)));
    col=vec3(r*g*sin(iTime*0.5),g*r*r*0.01,b*g*r);
    fragColor = vec4(col,1.0);
}
