float getGray(vec4 col)
{
    return (col.r + col.g + col.b)/3.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.0;
    vec4 col;

    if(uv.x > 1.0 && uv.y > 1.0){
        uv -= vec2(1.0, 1.0);
        col = texture(iChannel0, uv);
        col.g = (col.b + uv.x)/2.0; 
    }
    else if(uv.x > 1.0){
        uv -= vec2(1.0, 0.0);
        col = texture(iChannel0, uv);
        float gray = getGray(col);
        col = vec4(gray, gray, gray, gray);
    }
    else if(uv.y > 1.0){
        uv -= vec2(0.0, 1.0);
        col = texture(iChannel0, uv);
        col.r = (col.b + uv.x)/2.0; 
    }
    else{
        col = texture(iChannel0, uv);
        col.b = (col.r + col.g + uv.x)/3.0; 
    }

    // Output to screen
    fragColor = col;
}
