#ifdef GL_ES
precision mediump float;
#endif
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st =fragCoord/iResolution.xy;
    vec2 pos = (st-0.5)/vec2(1.,2);//矫正比例
    vec3 color = vec3(1.0);

    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);

    float angle = iTime;
    vec2 rotated = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * st;//颜色旋转矩阵
    vec3 rotated2 = vec3(rotated,1);

    float f = cos(a*8.);
    f = abs(cos(a*6.));

    color = vec3(1.-smoothstep(f,(f+0.2),r) )*rotated2;

    fragColor = vec4(color, 1.0);
}
