/*
277 characters by Dom Mandy in 2023
*/
void mainImage(out vec4 P, vec2 V) {
    float a, c, k, v, r = 9.;
    vec2 R = iResolution.xy,
         C = V + V - R;

    for (V = C /= R.y; k++ < 99.; V = C + exp(-a * V.y) * pow(v, V.r / 2.) * vec2(cos(c), sin(c))) {
        c = dot(V, vec2(a = atan(V.y, V.x), log(v = dot(V, V)) / 2.));
        r > v ? r = v, R = V : R;
    }

    P = isnan(v) ? P : sqrt(r + r * cos(atan(R.y, R.x) - vec4(0, 2, 4, 0)));
}
