{
    float tau = 6.28318531;
    vec2 uv = (pixlCoord / iResolution.xy) * 2.0 - vec2(1.0, 1.0);
    float power = length(uv);

    float b = tau;
    //float modTime = iTime - (b * floor(iTime/b));
    //float modTime = iTime / 100.0;
    float modTime = sin(iTime * 0.5);
    float brightness =
        0.75 * sin(modTime * power * tau)
        + 0.25 * tan(0.5 * tau + modTime * power * tau);

    vec3 color = vec3(
        0.0,
        0.3 * power * brightness,
        brightness
     );
     pixlColor = vec4(color, 1.0);
}
