#define MAX_STEPS 500
#define MAX_DIST 500.
#define SURFACE_DIST .001

float getDist(vec3 p)
{
    vec4 sphere = vec4(0, 0, 0, 1);

    p = mod(p, 4.) - 2.;

    float distSphere = length(p-sphere.xyz)-sphere.w;

    return distSphere;
}

vec3 getNormal(vec3 p)
{
    float dist = getDist(p);
    vec2 e = vec2(.01, 0);

    vec3 normal = dist - vec3(
        getDist(p-e.xyy),
        getDist(p-e.yxy),
        getDist(p-e.yyx)
    );

    return normalize(normal);
}


float rayMarch(vec3 begin, vec3 direction)
{
    float dist0 = 0.;
    int j = 0;

    for (int i = 0; i < MAX_STEPS; i++, j++)
    {
        vec3 p = begin + dist0 * direction;
        float distS = getDist(p);
        dist0 += distS;
        if (distS < SURFACE_DIST || dist0 > MAX_DIST) break;
    }

    if (j == MAX_STEPS || dist0 > MAX_DIST) return -1.;

    return dist0;
}

float getLight(vec3 p)
{
    vec3 light = vec3(1, 2, 0);
    vec3 normal = getNormal(p);

    float dif = clamp(dot(normal, light), 0.02, 1.);

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 begin = vec3(-2.+iTime, 3.-iTime, 0);
    vec3 direction = normalize(vec3(uv.x, uv.y, 1));
    vec3 col;

    float d = rayMarch(begin, direction);
    float dif;

    if (d < 0.)
    {
        col = vec3(0);
    }
    else
    {
        vec3 p = begin + direction * d;
        dif = getLight(p);
        col = vec3(dif);
    }

    col = pow(col, vec3(.4545));

    fragColor = vec4(col,1.0);
}
