#define GridSize 36.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 g = fragCoord*(3.1415/GridSize);
    float c = .5 + .25 * (cos(g.x+g.y) + cos(g.x-g.y)); // [0,1] range
    //float a = 1.1 * fragCoord.x/iResolution.x;
    vec2 d =  fragCoord.xy/iResolution.xy - vec2(0.5+.25*cos(iTime),0.5+.25*sin(.87*iTime));
    d *= vec2(1.0 + .5 * cos(.7 * iTime), 1.0 + .5 * cos(.6*iTime));
    float a = .1 / (.085 + d.x*d.x + d.y*d.y)-.1;
    float v = smoothstep(.0,0.1, (c-1.0) + a);

    vec3 colOff = vec3(0.0,0.0,0.0); 
    fragColor = vec4(colOff+v, 1.0); // Output to screen
}
