vec3 GetPixelDir(float fov, vec2 fragCoord)
{
    vec3 dir;
    dir.xy = fragCoord - iResolution.xy * 0.5;
    dir.z = iResolution.y * 0.5 / tan(radians(fov*0.5));
    return normalize(dir);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 dir = GetPixelDir(60.f, fragCoord);
    float cone = dot(dir, vec3(0.f, 0.f, 1.f));
    dir = acos(cone) < radians(30.f) ? dir : vec3(0.f);


    // Output to screen
    fragColor = vec4(dir,1.0);
}
