void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    xy.x = xy.x / iResolution.x;
    xy.y = xy.y / iResolution.y;
    vec4 solidRed = vec4(1.0,1.0,1.0,0.0);
    if((xy.x > 0.875) || ((xy.x > 0.125) && (xy.x < 0.25)) || ((xy.x > 0.5) && (xy.x < 0.625))) {
        solidRed.b = xy.x*cos(iTime);
        solidRed.g = xy.y*sin(iTime);
    }
    fragColor = solidRed;

}
