// === display =====================================================
// adapted from https://google.github.io/swissgl/#DotCamera

void mainImage( out vec4 O, vec2 u )
{
    vec2 U  = u / R.y, D;
    O *= 0.;
    O += .5 * texture(iChannel1, u/R ).a;             // display source image
 // O.r = texture(iChannel2, u/R ).z;                 // debug

    float m = 9.;                                     // display points
    for (int i=0; i < N*N; i++ )
        D = U - texelFetch(iChannel0, ivec2(i%N,i/N), 0).xy,
        m = min(m, dot(D,D));                         // get closest

    O += smoothstep( 2./R.y,0., sqrt(m) -.003 );      // paint dot
}

// === force field toward target ==================== 
// .xy: gradient
// .w : value

void mainImage( out vec4 O, vec2 u )
{
    O *= 0.;
    for (float i=0.; i<7.; i++ )               // large filter so that gradient acts at all scales
        O += textureLod(iChannel0, u/R, i );
    O /= 7.;

    float v = dot( O.rgb, vec3(.21,.72,.07) ); // luminance
    O = vec4( dFdx(v),dFdy(v),0,v);            
}

// === points ===============
// .xy = point coordinates

#define H(p) fract(sin((mod(p,6.28))*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)

void mainImage( out vec4 O, vec2 u )
{
    if ( int(min(u.x,u.y)) >= N ) discard;                // we track N*N points

    if (iFrame<1) {
        O = vec4( H(u)*R/R.y, 0,0 );                      // start: random location
        return;
      }
    O =  texelFetch(iChannel0, ivec2(u), 0);
    vec2 T = O.xy * R.y/R;                                // renormalize coordinates for texture access
    vec3 F = texture(iChannel2, T ).xyz;                  // read repulsion field
    if (F.z > 1.9 ) O.xy += .2* ( H(u+iTime) -.5 ) / R.y; // repulsion by other points
    F.xy = 1.*F.xy + 2.*texture(iChannel1, T ).xy;        // attraction by target image
    O.xy = clamp( O.xy + 30.*F.xy/R.y, vec2(0), R/R.y );  // new location

}

// === repulsing field around points ===================
// .xy : gradient
// .z  : value

void mainImage( out vec4 O, vec2 u )
{
    vec2 U  = u / R.y, D;
    O *= 0.;

    for (int i=0; i < N*N; i++ )                               // for each point
        D = U - texelFetch(iChannel0, ivec2(i%N,i/N), 0).xy,
        D *= float(N),
        O.xyz += vec3(D,1) * exp(-dot(D,D)*vec3(4,4,8));       // sum-up gradient + gaussian field
}
