float circle(float r, vec2 c, vec2 uv) {
  return distance(uv, c) - r;
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5 * iResolution.xy) / iResolution.y;

    uv.x = (uv.x) + (sin(iTime / 2.0) * 0.25);
    uv = abs(uv *8.0);

    vec3 x = vec3(1.0);
    int its = 12;
    for( int i = 0; i < its; i += 1 ) {

       float fft = (texture(iChannel0, vec2(0.1 * float(i), 0.25)).r) / 6.0;

       float r = (0.44 * float(i)) * cos(iTime) * 3.0;

       float woop = sin(uv.y - iTime);

       float warp = sin(iTime);

       float d = circle( r + warp + woop + fft, vec2(0.0), uv);

       float d2 = length(uv) - iTime + fft;

       vec3 y = pal( (iTime / 4.0) + d2, vec3(0.2,0.5,0.4),vec3(0.5,0.2,0.5),vec3(0.2, 0.2, 0.2),vec3(0.0,0.33,0.67) );

       // x *= (y / float(its)) * (1.0 - abs(d) * 20.0);
       x *= y * (1.0 - abs(d) * 2.0);
    }
    x /= float(its);

    fragColor = vec4(x, 1.0);
}
