void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float time = iTime * 0.5;
    vec3 c = vec3(0.0);

    for (int i = 0; i < 3; i++)
    {
        vec3 q = vec3(p.x, p.y, time + float(i));
        float r = length(q);
        float a = atan(q.y, q.x) + r + sin(time + r);
        float b = atan(q.z, length(q.xy)) + r + cos(time + r);

        c += vec3(0.5 + 0.5 * cos(4.0 * a + time)),
             vec3(0.5 + 0.5 * sin(7.0 * b - time)),
             vec3(0.5 + 0.5 * sin(4.0 * a + 7.0 * b + time));
    }

    fragColor = vec4(c / 3.0, 1.0);
}
