void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float colors[3];

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float seed = iTime;
    for (int i = 0; i < 3; i++) {
        seed += float(i);
        seed = float(int(seed * 300000.0 + uv.x * 500000.0 + uv.y * 50000.0) % 65535);
        seed /= 65536.0;
        seed = clamp(seed * 6.28, 0.0, 6.28);
        colors[i] = 0.5 + 0.5*cos(seed);
    }

    vec3 col = vec3(colors[0], colors[1], colors[2]);

    // Output to screen
    fragColor = vec4(col,1.0);
}
