float pointRay(vec2 uv, float size, float smoothing)
{
    float d = length(uv);


    d = smoothstep(size-smoothing, size+smoothing, d);

    return d;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return min(max(q.x,q.y),0.8);
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 mouse = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;

    // df col = white
    vec3 rls = vec3(1.); // white
    vec3 colC = vec3(1.);
    //mouse circle 
    float mouseCircle = step(.2,length(uv - mouse)-.1);
    // init box
    float box = sdBox(vec3(uv,0.) - vec3(1.,0.,0.),vec3(.15,.25,0.));
    float box1 = sdBox(vec3(uv,0.) - vec3(-1.,0.,0.),vec3(.15,.25,0.));
    float box2 = sdBox(vec3(uv,0.) - vec3(-1.,.6,0.),vec3(.15,1.-1.1,0.));
    float box3 = sdBox(vec3(uv,0.) - vec3(-1.,-.6,0.),vec3(.15,1.-1.1,0.));
    float ss = step(.2, length(uv)-.2);
    //add box
    rls *= box3 * box2 * box  ;

    rls /= pointRay(uv - mouse, 0.1, 0.6);



    // prepare texture
    vec3  tex1 = texture(iChannel0,uv).rgb;
    vec3  tex2 = texture(iChannel1,uv).rgb;

    vec3 finalRenderTex = vec3(0.);

    if(uv.x < 0.) {
        finalRenderTex = tex1;
    }else{
         finalRenderTex = tex2;
    }


  //  rls *=  box3 * box2 *  box ;
    rls = step(0.32, rls);
    fragColor = vec4(vec3(rls + finalRenderTex),1.);

}
