float sdf_circle(vec2 p, float r)
{
    return length(p) - r;
}

float sdf_scene(vec2 p)
{
    return sdf_circle(p, 0.0);
}

vec2 wobble(vec2 uv, int octaves, float bfreq)
{
    vec2 sum = vec2(0.0);
    for (int i = 1; i < octaves + 1; i++)
    {
        float gain = (1.0 + 0.1 * sin(0.2 * iTime)) / (8.0 * float(i));
        float freq = (bfreq + 0.2 * sin(0.1 * iTime)) * float(i);
        float freq_time = 0.05 * freq + (1.0 + 0.001 * sin(0.01 * iTime));
        float freq_uv = 1.01 * freq + (1.0 + 0.04 * sin(0.02 * iTime));
        float dif = 0.23;
        sum = sum + vec2(
            uv.x + gain * sin(iTime * freq_time + uv.y * freq_uv),
            uv.y + (gain + dif) * sin(iTime * (freq_time - dif) + uv.x * (freq_uv - dif))
        );
    }
    return sum;
}

#define DARK vec3(0.0)
#define LIGHT vec3(1.0, 0.42, 0.1)

#define GREN  vec3(0.3, 1.0, 0.2)
#define BLOO  vec3(0.3, 0.1, 1.0)
#define ORANJ vec3(1.0, 0.42, 0.1)
#define WHIT vec3(1.0, 1.0, 1.0)

#define LOREZ 0.0000000001

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy/2.0)/iResolution.yy;
    vec2 uvn = uv;

    uv = wobble(uv, 3, 3.14);
    uv = uv + 0.1 * wobble(uv, 1, 12.14);
    uv = wobble(uv, 1, 1.14);
    float mord = 0.3 + 0.29 * sin(0.09 * iTime + 4.0 * length(uv));

    // Output to screen
    float modctrl = 0.4 + 0.3 * sin(0.0094 * iTime + 4.3 * length(uv) * (1.0 + 0.5 * sin(0.09 * iTime)));
    // fragColor = vec4(smoothstep(0.0499, 0.0501, mod(sdf_scene(uv), modctrl)));

    vec3 c0 = vec3(float(int(8.0 * (0.5 + 0.5 * sin(0.203 * iTime + 0.1)))) / 7.0);
    vec3 c1 = mix(ORANJ, BLOO, float(int(8.0 * (0.5 + 0.5 * sin(0.123 * iTime + 0.1)))) / 7.0);
    vec3 c2 = mix(WHIT, GREN, float(int(8.0 * (0.5 + 0.5 * sin(0.309 * iTime + 0.9)))) / 7.0);

    float dc = fwidth(modctrl) * 0.5;
    float modder = smoothstep(mord-dc, mord+dc, mod(sdf_scene(uv), modctrl));
    vec3 dark = mix(c0, c2, smoothstep(-dc, dc, sin(0.4 * iTime + 3.0 + length(uv))));
    vec3 light = c1;
    fragColor = vec4(mix(dark, light, modder), 1.0);
}
