vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.8,0.9,0.3);

    return a + b*cos( 6.28318*(c*t+d));
}

vec3 ray(vec2 uv, float startTime, float moved)
{
    float speed = 5.;
    float acc = 400.;
    float movedDist = 20. + moved * speed + 0.5 * acc * moved * moved;

    float deg = startTime;

    vec2 center = vec2(iResolution.x / 2.,iResolution.y / 2.);
    vec2 endPos = vec2(center.x + cos(deg * (8. + sin(startTime) * 2.)) * movedDist, center.y + sin(deg * (8. + sin(startTime) * 2.)) * movedDist);

    float inner = dot(endPos - center, uv - center) / length(endPos - center) / length(uv - center);
    float dEnd = (endPos.x - center.x) * (endPos.x - center.x) + (endPos.y - center.y) * (endPos.y - center.y);
    float dUv = (uv.x - center.x) * (uv.x - center.x) + (uv.y - center.y) * (uv.y - center.y);
    float dist = (endPos.x - uv.x) * (endPos.x - uv.x) + (endPos.y - uv.y) * (endPos.y - uv.y);

    float dCut = step(dUv, dEnd) * step(dist, 600. * pow(moved, 8.));
    float innerCut = step(0.9999 + sqrt(dist) / 1000000., inner);


    float p = innerCut * dCut;
    vec3 endColor = palette(sqrt(dUv) / (500. + sin(startTime) * 200.));

    return p * endColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec3 color = vec3(0., 0., 0.);
    vec2 coord = fragCoord;
    float t = iTime + 1000.;

    for (int i = 0; i < 300; i++)
    {
        float shootTime = trunc(t / 0.01) * 0.01 - float(i) * 0.01;
        color += ray(coord, shootTime, t - shootTime);
    }

    fragColor = vec4(color, 1.);
}
