mat3 transpose3(mat3 m) {
  return mat3(m[0][0], m[1][0], m[2][0],
              m[0][1], m[1][1], m[2][1],
              m[0][2], m[1][2], m[2][2]);
}

mat3 MATRIX_CVD_DEUTERANOMALY_12NM = mat3(
     0.45777631, 0.73181524, -0.18959155,
     0.22640451, 0.73103851,  0.04255698,
    -0.01159912, 0.03433304,  0.97726608
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 texColor = texture(iChannel0, uv);
    texColor.rgb = transpose3(MATRIX_CVD_DEUTERANOMALY_12NM) * texColor.rgb;

    fragColor = vec4(texColor);
}
