void mainImage( out vec4 O, in vec2 U )
{
    vec2 r = iResolution.xy,u=U/r.xy;float t=iTime;O = vec4(0);
    for(float i = -4.; i < 5.0;  i++ )
        for(float i2 = -4.0,a; i2 < 5.0; a = i+i2, O.rgb += (.5 + .5 * cos(t+u.xyx * a+vec3(0,2,4))) 
            * ( 1.0 - smoothstep(
                                     0.01 * abs(sin(t * 15.5 + a)), 
                                     0.06 + 0.09 * abs(sin(t + a) * 0.5 ),
                                     length(
                                               U/vec2(r.x/2.,r.y) - vec2(
                                                                           1.0 + i * .09 + sin(t * 1.5 + i * .5 + u.y) * .05,
                                                                            .5 - i2++ * .09 + cos(t * 1.5 + i2 * .5 + u.x) * .05
                                                                        )
                                           )
                                )
              )
           )
           {}  
}
