mat2 rotation(float angle){
    return mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
}

float selectColumn(vec2 st, float nColumn, float selectColumn){
    float b = step(selectColumn, mod(st.x, nColumn));
	return 1.-b;
}
float selectRow(vec2 st, float nColumn, float selectColumn){
    float b = step(selectColumn, mod(st.y, nColumn));
	return 1.-b;
}

vec2 routedPieces(vec2 st){
    float c = selectColumn(st, 2., 1.);
    float r = 1.-selectRow(st,2., 1.);
    float c2 = 1.-selectColumn(st, 2., 1.);

    //Columns c is routed.
    st= st*rotation(radians(90.*c));
    //Rows r is routed.
    st = st*rotation(radians(90.*r));
    //Intersection Columns c2 and rows r is routed.
    st = st*rotation(radians(180.*c2*r));
    return st;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    float zoom = 8.;
    st *= zoom;

    st = routedPieces(st);

	float triangle = fract(st.x)+fract(st.y);
    triangle = step(1., triangle);

    float colorRow = selectRow(st, 4., 2.);
    float colorColumn = selectColumn(st, 4., 2.);

    float clocksColorRowColumns = abs(colorColumn-abs(colorRow-triangle));
    vec3 color = vec3(clocksColorRowColumns);

    fragColor = vec4(color,1.0);
}
