const float pi = acos(-1.0);
float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float AA=3.0/min(iResolution.x, iResolution.y);
    vec3 col=vec3(0.0);
    float d=0.0;

    for(float i=0.0;i<3.0;i++)
    {
        if( iMouse.z>0.001 )
        {
            d = sdCircle(p,i/3.);
            col = mix(col, vec3(1.0), smoothstep(AA,0.0,abs(d)));
        }
        vec2 r_p=vec2(cos(iTime),sin(iTime))*i/3.;
        d=sdCircle(p+r_p,0.05);
        col=(d>0.0) ? col:vec3(1.0,0.0,0.0);
        for(float j=0.0;j<3.0;j++)
        {
            if( iMouse.z>0.001 )
            {
                d = sdCircle(p+r_p,j/3.);
                col = mix(col, vec3(0.8), smoothstep(AA,0.0,abs(d)));
            }
            vec2 b_p=vec2(cos(iTime*4.),sin(iTime*4.))*j/3.;
            d=sdCircle(p+r_p+b_p,0.025);
            col=(d>0.0) ? col:vec3(0.0,1.0,1.0);
        }
    }

	fragColor = vec4(col,1.0);
}
