float breakpointOne=0.;
float breakpointTwo=.3;
float breakpointThree=.5;
float breakpointFour=.7;

float map(float value,float min1,float max1,float min2,float max2){
    return min2+(value-min1)*(max2-min2)/(max1-min1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = fragCoord/iResolution.xy;
    vec4 firstColor =vec4(.8902,.8902,.8902,1.);
    vec4 middleColor =vec4(.0745,.4745,.9059,1.);
    vec4 endColor =vec4(.8863*sin(iTime*.5),.6627*sin(iTime*.8),.8196*sin(iTime*.2),1.);


    vec4 col=vec4(0.);

    if(xy.y<breakpointTwo){
        float mixParam=map(xy.y,breakpointOne,breakpointTwo,0.,1.);
        col=mix(firstColor,middleColor,mixParam);
    }else if(xy.y<breakpointThree){
        float mixParam=map(xy.y,breakpointTwo,breakpointThree,0.,1.);
        col=mix(middleColor,endColor,mixParam);
    }else if(xy.y<breakpointFour){
        float mixParam=map(xy.y,breakpointThree,breakpointFour,0.,1.);
        col=mix(endColor,middleColor,mixParam);
    }else{
        float mixParam=map(xy.y,breakpointFour,1.,0.,1.);
        col=mix(middleColor,firstColor,mixParam);
    }



    fragColor = vec4(col);
}
