// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 300
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float Length(vec2 p, float k) {
    p = abs(p);
    return pow(pow(p.x, k)+pow(p.y, k), 1./k);
}

float sdTorus(vec3 p, float r1, float r2, float k, float k2){
    return Length(vec2(Length(p.xz, k)-r1, p.y), k2)-r2;
}
float Hash21(vec2 p){
    p = fract(p*vec2(342.234, 152.251));
    p += dot(p, p+1423.12);
    return fract(p.x*p.y);
}
vec3 Transform(vec3 p, float time){
    p.x = p.x + time;
    return p;
}
float TruchetLayer(vec3 p, float k, float k2, float wobble) {
    float grid = abs(p.y)-.01;

    vec3 P = p;
    vec2 id = floor(P.xz);
    P.xz = fract(P.xz)-.5;
    float box = sdBox(P, vec3(.49));

    //d = min(d, grid); // boolean union
    grid = max(grid, -box); // boolean difference

    float n = Hash21(id);
    P.x *= n<0.5 ? -1.: 1.;
    float s = P.x>-P.z ? 1. : -1.;
    P.xz -= .5*s;

    p = Transform(p, -iTime);
    float cd = max(-0.5,mix(0.1,.03, length(abs(p))*0.9)); // center of screen

    float a = atan(P.x,P.z); // polar coordinate from -pi to pi
    P.y += cos(a*2.)*wobble;
    float d = sdTorus(P, 0.5, .05+cd*1., k+cd*wobble*50., k2);

    //d = min(grid, d);

    return d;
}
vec2 Truchet(vec3 p){
    float t1 = TruchetLayer(p, 1.4, 2., .1);
    float t2 = TruchetLayer(p-vec3(.5,0,.5), 1., 1.3, 0.) * .7 - .02;
    return vec2(t1,t2);
}
float GetDist(vec3 p) {
    p = Transform(p, iTime);
    vec2 t = Truchet(p);
    float d = min(t.x, t.y) * .8;
    d = min(d, p.y+0.3);
    //d = t1;
    return d;
}
float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy+vec2(0.066,0.1);

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);
        float cd = mix(0.,1., length(abs(p)))*1.3; // center of screen
        p = Transform(p, iTime);
        vec2 t = Truchet(p) * cd;
        if(p.y<-.25){ // ground
            t.y = pow(t.y,0.6);
            col *= t.y*2.;
            col *= 0.8;
        } else if (t.x<t.y) { // truchet
            col *= vec3(1.2,.05,.05);
        }
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
