#define INTENSITY 2.0

float rand(vec2 co)
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c) * INTENSITY;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 offset;
    offset.x = rand(fragCoord+vec2(iTime));
    offset.y = rand(fragCoord+vec2(iTime+1.));
    vec2 uv = (fragCoord+offset)/iResolution.xy;
    vec4 color = texture(iChannel0, uv);
    fragColor = vec4(vec3(step(0.5,color.r)),1.0);
}
