void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0  - iResolution.xy) / iResolution.y;
    //uv = fract(uv * 2.) - 1.5;

    float radius=60.0 + sin(iTime)*50.0;

    vec2 uvMove = vec2(uv.x+sin(iTime*.21), uv.y+cos(iTime*.32));
    float dr = step(sin(length(uvMove)*radius), 0.);

    radius=60.0 + sin(iTime*.8)*50.0;
    uvMove = vec2(uv.x+sin(iTime*.23), uv.y+cos(iTime*.39));
    float db = step(sin(length(uvMove)*radius), 0.);;

    radius=60.0 + sin(iTime*.7)*50.0;
    uvMove = vec2(uv.x+sin(iTime*.25), uv.y+cos(iTime*.37));
    float dg = step(sin(length(uvMove)*radius), 0.);;


    // Output to screen
    fragColor = vec4(dr, dg, db, 1.0);
}
