#define R(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,h; vec3 p,q=vec3(-1,1,1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)/1.2;
    p.z-=.4;
    p=R(iTime);

    for(j=0.;j++<4.;)
        p=abs(abs(p)-.05)-.07,
        d=length(p-q*h)-.05;

    e+=d;
}o+=vec4(-log(p*q),1)/i;
}
