void mainImage(out vec4 o, vec2 I)
{
    I+=I-iResolution.xy;
    I*=mat2(cos(round(atan(I.y,I.x)*.95)/.955+vec4(0,11,33,0)));
    o=cos(mod(ceil(I.y/.3/I)+ceil(I=log(I)/.5-iTime),4.).x+vec4(6,5,2,0))+1.;
    o/=2.+max(I=fract(I)-.3,-I-I).x/.4;
}

