#define wmod(p, w) (mod(p + w/2., w) - w/2.)
#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define PI     3.1415

void mainImage(out vec4 o, vec2 u) {
    vec3 R = iResolution, q, D;

    float d, s, ss, f, i, j, h,
          e = 1e-2, t = iTime;

    u = (u - R.xy * .5) / R.y;
    D = vec3(u, 1.5);
    o *= 0.;

    while(i++ < 44.) {
        q = d * D;
        q.z  -= cos(cos(t * .1) * 7.) * 15.,
        q.yz *= rot(cos(t * .7) * .1);
        q.zx *= rot(cos(t * .1) * 2.);
        q.zy *= 1. - dot(q, q) * .001;

        ss = abs(q.y);

        j = 4.;
        while(j < 128.) 
            h = wmod(sin(j * .3), .6) * 10.,
            q.xz *= h,
            s = dot(cos(q.xz), vec2(.1)) / h ,
            ss += abs(s * 2.) - .4,
            j += j;

        ss = abs(ss) * .5 - .01,

        f = .5 - ss / e, 
        s = f / d * i / 400.,

        f > .0
            ? o += s, s
            : o.r += .002;

        d += max(ss, e / 5.);
    }
}
