void mainImage(out vec4 O, vec2 I)
{
    vec2 r = iResolution.xy,
    u = I+I-r,
    p = vec2(log(length(u/=r.y)),atan(u,u.yx))/.1;
    O = tanh(vec4(1,.3,.1,0)*.3 / length(cos(p.x-=iTime)-sin(p)) / length(u));
}
