#define fb vec3(117,170,219)/255.0
#define fs vec3(252,191,69)/255.0
#define fs2 vec3(132,53,17)/255.0

#define pw 1.0/iResolution.x

float sd(vec2 uv)
{
    float st = 1.0 - sin(atan(uv.y, uv.x) * 32.0) + length(uv * 1.25) + 0.25;
    st = length( clamp( st, 0.65, 1.5));
    float sun = smoothstep(-pw, pw, (length(uv) * st) - 0.25);
    return sun;
}
vec3 f(vec2 uv)
{
    vec2 rs = vec2(2.0,0.4);
    float sha = length(max(abs(uv)-rs,0.0));

    float sun = sd(uv);

    float r = smoothstep(-pw, pw, sha);
    vec3 col = mix(vec3(1.0) * 100.0 , fb, r);

    col = mix(fs2, col, sd(uv + vec2(0.0, -0.0075)));
    col = mix(fs2, col, sd(uv + vec2(-0.0075,0.0)));
    col = mix(fs2, col, sd(uv + vec2(0.0075,0.0)));
    col = mix(fs2, col, sd(uv + vec2(0.0,0.0075)));
    col = mix(fs, col, sun);

    return col;//*(0.9+(abs(sin(uv.x * 2.25 + iTime))*0.5+0.5)*0.35);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    uv.y += sin(uv.x * 2.25 + iTime) * 0.07;
    vec3 flag = f(uv);

    fragColor = vec4(pow(flag,vec3(1.0/2.2)), 1.0);
}
