vec2 triangle_wave(vec2 a){
    return abs(fract((a+vec2(1.,0.5))*1.6)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 16.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    bool t3 = false;
    for(int k = 0; k < 12; k++){
        t3 = uv.x > uv.y || !t3;
        uv = (uv+t2)/scale+float(!t3)/2.;
        t2 = triangle_wave(uv+.6);
        uv = t2-triangle_wave(uv.yx);
        float c1 = abs(uv.x-uv.y);
        col = col.yzx;  
        if(uv.y > uv.x) col = vec3(col.yz,c1);
    }
    fragColor = vec4(abs(col)*2.,1.0);
}
