renderpass.code:
float spiralSDF(vec2 st, float t) {
    st -= .5;
    float r = dot(st,st);
    float a = atan(st.y,st.x);
    return abs(sin(fract(log(r)*t+a*0.159)));
}

vec2 rotate(vec2 st, float a) {
    st = mat2(cos(a),-sin(a), sin(a), cos(a))*(st-.5);
    return st+.5;
}

float rectSDF(vec2 st, vec2 s) {
    st = st*2.-1.;
    return max(abs(st.x/s.x),abs(st.y/s.y));
}

float stroke(float x, float s, float w) {
    float d = step(s, x+w*.5)- step (s, x-w*.5);
    return clamp(d, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{






    vec3 color = vec3(0.);
    vec2 uv =3. * fragCoord/iResolution.xy -1.;
    uv.x *= iResolution.x/iResolution.y;
    vec2 uv2 =2. * fragCoord/iResolution.xy -1.;
    uv2.x *= iResolution.x/iResolution.y;
    vec2 st = rotate(uv, iTime*3.);
    vec2 st2 = rotate(st, 2.);
    vec2 st3 = rotate(st2, 2.);
    vec2 nst = rotate(uv, -iTime*2.);

    float p_angle = atan(uv2.x, uv2.y);
    float p_distance = length(uv2) * 2.;
    vec2 tst = vec2(p_distance, p_angle);
    vec3 tcol = texture (iChannel0, vec2(1.0 / tst.x + iTime, tst.y)).xyz;
    tcol *= min(tst.x*.1, 1.0);

    vec3 col = 0.5 + 0.2*cos(iTime-st.x+vec3(0,2,4));
    vec3 col2 = 0.5 + 0.2*cos(iTime-st2.x+vec3(0,2,4));
    vec3 col3 = 0.5 + 0.2*cos(iTime-st3.x+vec3(0,2,4));
    vec3 col4 = 0.5 + 0.2*cos(iTime-st3.x+vec3(0,2,4));

    float s = rectSDF(nst, vec2(1.));

    vec3 c = vec3(0.0);

    c+= stroke(s, 1., .3) + stroke(s, 3., 1.);





    color += (.5,spiralSDF(st,.14))*col;
    color += (.5,spiralSDF(st2,.14))*col2;
    color += (.5,spiralSDF(st3,.14))*col3 + (tcol*.6) + c;







    fragColor = vec4(color,1.0);
}
