#define TILE (iResolution.x/48.0)
#define SND(a) (texture(iChannel0, vec2(a, 0.)).x-0.25)


float snap(float v, float w) {
    return floor(v * w) / w;
}

float plot(float y, vec2 uv, vec2 r) {
    float v = y / r.y;




    float thick = (1.0/TILE)*2.0;
    float feather = thick*0.05;

    uv.y -= 0.5;

    float top = v-uv.y;
    float bot = (v-thick)-uv.y;
    return clamp(smoothstep(thick-feather, thick+feather, top) -
     smoothstep(thick-feather, thick+feather, bot), 0.0, 1.0);
}



void mainImage( out vec4 fragColor, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 R = iResolution.xy;
    vec2 uv = fc/iResolution.xy;



    vec4 m = iMouse * float(iMouse.z > 0.01);
    float mx = m.x / R.x;

    float snd = SND(snap(uv.x, 64.0));
    float valueToPlot = snd*(iResolution.y*0.9);//(cos(((uv.x * (1.0+(mx*4.0)))+(iTime*0.2))*TILE)*24.0);

    float c = m.z > 0.01 ? m.x : (R.x*0.5);

    float plt = plot(valueToPlot, uv, iResolution.xy);

    vec3 sndColor = mix(vec3(0.0, 1.0, 0.0), vec3(1.0, 0.0, 0.0), clamp(smoothstep(0.0, 1.0, snd*3.14), 0.0, 1.0));
    col = mix(col, sndColor, plt);




    fragColor = vec4(col,1.0);
}
