//Generated with the help of OpenAI ChatGPT 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.y);
    vec2 offset = vec2(-1.0,-0.5);
    uv += offset;
    float t = iTime * 2.0;
    float c = cos(t);
    float s = sin(t);
    mat2 m = mat2(c, -s, s, c);
    uv = m * uv;
    uv -= vec2(0.5);

    vec3 pos = vec3(uv.x, uv.y, 0.0);
    vec3 color = vec3(1.0, 3.0, 5.0);
    float size = 0.03;
    float dist = length(pos);
    vec3 outColor = vec3(0.0);
    for(int i = 0; i < 100; i++) 
    {
        float sinVal = sin((iTime + float(i)) * 1.00 );
        pos += vec3(0.01, sinVal * 0.5, 0);
        dist = length(pos);
        outColor += smoothstep(size,0.0, dist) *color;
    }
    fragColor = vec4(outColor, 1.0);
}
