float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 base = vec3(1.0,1.0,1.0);
    vec3 scan = vec3(1.0,1.0,1.0);

    float rnd = hash11(float(iFrame));

    vec3 cyan = texture(iChannel1, uv).rgb;
    vec3 magenta = texture(iChannel2, uv).rgb;
    vec3 yellow = texture(iChannel3, uv).rgb;

    // Prints each color layer onto the film base (Color Correction Multipliers)
    base -= cyan;
    base -= magenta;
    base -= yellow;

    // Emulates film scanning
    scan -= vec3(0.0,base.gb);
    scan -= vec3(base.r,0.0,base.b);
    scan -= vec3(base.rg,0.0);
    scan += 1.2;
    scan *= 0.135;
    scan += 0.16;

    // Output to screen
    fragColor = vec4(scan, 1.0);
}

