float aa = 0.1;
float radius = 0.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    float d = length(uv);

    aa = (sin(iTime) + 1.0) * 0.5;
    float mask = smoothstep(radius + aa, radius - aa, d);
    vec4 canvas = vec4(mask);

    fragColor = canvas;
}
