void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //test effect about smoothstep (min,max,t) and smoothstep(max,min,t)
    float t1 = smoothstep(0.0,1.0,uv.x);
    float t2 = smoothstep(1.0,0.0,uv.x);

    float v1 = smoothstep(0.0,0.01,0.02 - abs(uv.y - t1));
    float v2 = smoothstep(0.0,0.01,0.02 - abs(uv.y - t2));

    vec3 col = vec3(v1,v2, max(v1,v2) * (0.5 + 0.5 * cos((iTime + (1.0-uv.x)) * 10.0)));

    col = pow(col,vec3(2.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}
