void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    if (uv.y > uv.x && uv.y < 1.0 - uv.x) {
        fragColor = vec4(0.0, 1.0, 1.0, 1.0); // Star and blue
    } else {
        // White and red stripes
        // Number of stripes
        float numStripes = 5.0;

        // Calculate the width of each stripe
        float stripeWidth = 1.0 / numStripes;

        // Calculate the stripe index based on the UV coordinate
        float stripeIndex = floor(uv.y / stripeWidth);

        // Calculate the black or white color based on the stripe index
        vec3 stripeColor = mod(stripeIndex, 2.0) < 1.0 ? vec3(1.0, 0., 0.) : vec3(1.0);

        fragColor = vec4(stripeColor, 1.0);
    }
}
