float bias(float a, float bias) {
  return a / ((1.0 / bias - 2.0) * (1.0 - a) + 1.0);
}

float gain(float a, float gain) {
  return (a < 0.5f) ? bias(a * 2.0, gain) / 2.0
                    : bias(a * 2.0 - 1.0, 1.0 - gain) / 2.0 + 0.5f;
}

vec3 contrast(vec3 c, float p)
{
    return vec3(gain(c.r, p), gain(c.g, p), gain(c.b, p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;

    col = contrast(col, iMouse.x/iResolution.x * 2.0 - 1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
