float getDistance( in vec2 point1, in vec2 point2)
{
    return (sqrt(pow(point2.x - point1.x, 2.0) + pow(point2.y - point1.y, 2.0)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iMouse.xy;
    vec3 col = vec3(0.0,0.0,0.0);

    float dst = getDistance(fragCoord, center);

    float distort =     sin((fragCoord.x / 10.0) + sin(iTime*2.0) * 10.0) * 20.0;
    distort = distort + cos((fragCoord.y / 10.0) + cos(iTime*2.0) * 10.0) * 20.0;

    float distorted = dst + distort;

    bool doColor = dst + distort < iResolution.y / 4.0;

    float scaledDist = distorted/(iResolution.y/4.0);

    if(doColor){
        col = vec3(clamp(scaledDist, 0.0, 1.0));
    }


    col = vec3(col.x * sin(iTime - 0.5), col.y * sin(scaledDist + (iTime + 0.5)), col.z * sin(scaledDist + iTime + 2.0));

    // Output to screen
    fragColor = vec4(col,1.0);
}
