void mainImage(out vec4 o, in vec2 C){
    vec3 p=vec3(.5,-iTime,iTime);
    for(float c=1.,i;i++<999.&&c>.001;p+=c*vec3((C.xy*2.-iResolution.xy)/iResolution.y,1.)*.1)
        c=length(min(fract(p),1.-fract(p))-.2)+sin(5.*p.x)*sin(5.*p.y)*sin(5.*p.z)*.3,
        o=vec4(sin(p.y+vec3(0,2,4))*.5+.5,1.);
}
