// https://www.shadertoy.com/user/timestamp
float sdLoopedSquare(vec2 p)
{
    p = abs(p) - .5;
    vec2 q = abs(p + .25);
    return max(p.x,p.y) > 0.
        ? abs(length(p) - .25)
        : min(q.x, q.y);    
}

