#define V vec3
void mainImage( out vec4 O, vec2 u)
{
    V R = iResolution;

    for( float w = min(R.x,R.y), d, i=0.; i<60. ;i++)
        d = length( u+u-R.xy + sin(mod(i,3.))* i*.04*w) - i*.07*w,
        d > 0. ? 
            O.rgb = V[](V(0,.3,.5),
                        V(.6,0,.4),
                        V(.8,.9,.8), 
                        V(0,.4,.5), 
                        V(.6)
                        ) [int(i)%5] : R,
     O *= min(1.,abs(d)/3.);
}
